import {
  ActionIcon,
  Box,
  Button,
  Code,
  Flex,
  Group,
  Select,
  Space,
  Text,
  TextInput,
} from "@mantine/core";
import { TimeInput } from "@mantine/dates";
import { useForm } from "@mantine/form";
import { randomId } from "@mantine/hooks";
import { IconClock, IconTrash } from "@tabler/icons-react";
import React, { FC, useCallback, useEffect, useRef } from "react";
import { API_CONFIG } from "shared/constants/api";
import httpService from "shared/services/http.service";

interface IProps {
  token: any;
}

const CheckIn: FC<IProps> = ({ token }) => {
  const ref = useRef<HTMLInputElement>();

  const getUserDetails = useCallback(() => {
    httpService
      .get(`${API_CONFIG.path.getUserDetails}?token=${token}`)

      .then((res) => {
        // setUserDetails(res.findUser[0]);
        console.log("res.findUser[0]:", res.findUser[0]);
      })
      .catch((err) => {
        // err?.response?.status && setStatusCode(err?.response.status || "");
        console.error(err);
      });
  }, []);

  useEffect(() => {
    getUserDetails();
  }, []);

  const form = useForm({
    initialValues: {
      employees: [{ name: "", key: randomId() }],
    },
  });

  const fields = form.values.employees.map((item, index) => (
    <Group key={item.key} mt="xs">
      <Select
        placeholder="Pick one"
        searchable
        nothingFound="No options"
        data={["React", "Angular", "Svelte", "Vue"]}
      />
      <TextInput
        placeholder="John Doe"
        withAsterisk
        sx={{ flex: 1 }}
        {...form.getInputProps(`employees.${index}.name`)}
      />
      <ActionIcon
        color="red"
        onClick={() => form.removeListItem("employees", index)}
      >
        <IconTrash size="1rem" />
      </ActionIcon>
    </Group>
  ));

  return (
    <Flex direction="row" justify="start">
      <TimeInput
        label="Time (24 hour)"
        ref={ref}
        rightSection={
          <ActionIcon onClick={() => ref.current.showPicker()}>
            <IconClock size="1rem" stroke={1.5} />
          </ActionIcon>
        }
        maw={105}
        // mx="auto"
        withAsterisk
      />
      <Space h="lg" />
      <Button variant="outline" color="cyan" sx={{ width: "105px" }}>
        Add Task
      </Button>

      <Box maw={500} mx="auto">
        {fields.length > 0 ? (
          <Group mb="xs"></Group>
        ) : (
          <Text color="dimmed" align="center">
            No one here...
          </Text>
        )}

        {fields}

        <Group position="center" mt="md">
          <Button
            variant="outline"
            color="cyan"
            sx={{ width: "105px" }}
            onClick={() =>
              form.insertListItem("employees", {
                name: "",
                key: randomId(),
              })
            }
          >
            Add Task
          </Button>
        </Group>

        <Text size="sm" weight={500} mt="md">
          Form values:
        </Text>
        <Code block>{JSON.stringify(form.values, null, 2)}</Code>
      </Box>
    </Flex>
  );
};

export default CheckIn;
